import React, { useState, useEffect } from 'react';
import axios from 'axios';
import SubcategoryForm from '../forms/SubcategoryForm';

const SubcategoryManagement = () => {
    const [subcategories, setSubcategories] = useState([]);
    const [categories, setCategories] = useState([]);

    useEffect(() => {
        fetchCategories();
        fetchSubcategories();
    }, []);

    const fetchCategories = async () => {
        try {
            const response = await axios.get('http://localhost:3001/api/categories');
            setCategories(response.data);
        } catch (error) {
            console.error('Error fetching categories:', error);
        }
    };

    const fetchSubcategories = async () => {
        try {
            const response = await axios.get('http://localhost:3001/api/subcategories');
            setSubcategories(response.data);
        } catch (error) {
            console.error('Error fetching subcategories:', error);
        }
    };

    const addSubcategory = async (newSubcategory) => {
        try {
            const response = await axios.post('http://localhost:3001/api/subcategories', {
                name: newSubcategory.name, // Ensure field names match your API expectations
                CategoryId: newSubcategory.CategoryId, // Ensure field names match your API expectations
            });
            setSubcategories([...subcategories, response.data]);
        } catch (error) {
            console.error('Error adding subcategory:', error);
        }
    };

    return (
        <div className="subcategory-management">
            <h1>Subcategory Management</h1>
            <SubcategoryForm onSubmit={addSubcategory} categories={categories} />
            <ul>
                {subcategories.map((subcategory) => (
                    <li key={subcategory.id}>{subcategory.name}</li>
                ))}
            </ul>
        </div>
    );
};

export default SubcategoryManagement;
