import React, { useState, useEffect } from "react";
import { NavDropdown } from "react-bootstrap";
import axios from "axios";

function CategoriesDropdown() {
    const [categories, setCategories] = useState([]);

    useEffect(() => {
        // Fetch categories from the API
        axios.get("http://localhost:3001/api/categories")
            .then((response) => {
                setCategories(response.data);
            })
            .catch((error) => {
                console.error("Error fetching categories:", error);
            });
    }, []);

    return (
        <NavDropdown title="Product Categories" id="category-dropdown">
            {categories.map((category) => (
                <CategoryDropdown key={category.id} category={category} />
            ))}
        </NavDropdown>
    );
}

function CategoryDropdown({ category }) {
    const [subcategories, setSubcategories] = useState([]);

    useEffect(() => {
        // Fetch subcategories for the current category from the API
        axios.get(`http://localhost:3001/api/categories/${category.id}/subcategories`)
            .then((response) => {
                setSubcategories(response.data);
            })
            .catch((error) => {
                console.error("Error fetching subcategories:", error);
            });
    }, [category.id]);

    return (
        <NavDropdown.Item href={`#category/${category.id}`}>
            {category.name}
            <SubcategoriesDropdown subcategories={subcategories} />
        </NavDropdown.Item>
    );
}

function SubcategoriesDropdown({ subcategories }) {
    return (
        <NavDropdown.Menu>
            {subcategories.map((subcategory) => (
                <NavDropdown.Item key={subcategory.id} href={`#subcategory/${subcategory.id}`}>
                    {subcategory.name}
                </NavDropdown.Item>
            ))}
        </NavDropdown.Menu>
    );
}

export default CategoriesDropdown;
