import React, { useState } from 'react';
import { Container, Row, Col, Navbar, Nav, NavDropdown, Card } from 'react-bootstrap';

function MainPage() {
    const [selectedCategory, setSelectedCategory] = useState(null);

    // Replace this with actual data fetched from your database
    const products = [
        { id: 1, name: 'Product 1', category: 'Category 1' },
        { id: 2, name: 'Product 2', category: 'Category 2' },
        // Add more product data here
    ];

    // Get unique categories from product data
    const categories = [...new Set(products.map((product) => product.category))];

    // Filter products based on the selected category
    const filteredProducts = selectedCategory
        ? products.filter((product) => product.category === selectedCategory)
        : products;

    return (
        <Container fluid>
            {/* Top Navigation Bar */}
            <Navbar bg="light" expand="lg">
                <Navbar.Brand href="#">Your Online Store</Navbar.Brand>
                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="ml-auto">
                        <Nav.Link href="#">Home</Nav.Link>
                        {/* Add more top navigation links */}
                    </Nav>
                </Navbar.Collapse>
            </Navbar>

            <Row>
                {/* Sidebar */}
                <Col sm={3}>
                    <Nav variant="pills" className="flex-column">
                        <NavDropdown title="Product Categories" id="category-dropdown">
                            {categories.map((category) => (
                                <NavDropdown.Item
                                    key={category}
                                    onClick={() => setSelectedCategory(category)}
                                >
                                    {category}
                                </NavDropdown.Item>
                            ))}
                        </NavDropdown>
                    </Nav>
                </Col>

                {/* Main Content */}
                <Col sm={9}>
                    <Row>
                        {filteredProducts.map((product) => (
                            <Col key={product.id} sm={4}>
                                <Card>
                                    <Card.Body>
                                        <Card.Title>{product.name}</Card.Title>
                                        <Card.Subtitle className="mb-2 text-muted">
                                            {product.category}
                                        </Card.Subtitle>
                                        {/* Add more product details */}
                                    </Card.Body>
                                </Card>
                            </Col>
                        ))}
                    </Row>
                </Col>
            </Row>
        </Container>
    );
}

export default MainPage;
