import React, { useState, useEffect } from 'react';
import CategoryForm from '../forms/CategoryForm';
import axios from 'axios'; // Import Axios for making API requests
import {HOST} from "../../constants"
const CategoryManagement = () => {
    const [categories, setCategories] = useState([]);

    useEffect(() => {
        // Fetch categories from the API when the component mounts
        fetchCategories();
    }, []);

    const fetchCategories = async () => {
        try {
            // Make a GET request to fetch categories from the API
            const response = await axios.get(`${HOST}/api/categories`); // Replace with your API endpoint
            setCategories(response.data);
        } catch (error) {
            console.error('Error fetching categories:', error);
        }
    };

    const addCategory = async (newCategory) => {
        try {
            // Make a POST request to add the new category to the database
            const response = await axios.post(`${HOST}/api/categories`, newCategory); // Replace with your API endpoint
            setCategories([...categories, response.data]);
        } catch (error) {
            console.error('Error adding category:', error);
        }
    };

    return (
        <div className="category-management">
            <h1>Category Management</h1>
            <CategoryForm onSubmit={addCategory} />
            <ul>
                {categories.map((category, index) => (
                    <li key={category.category_id}>{category.name}</li>
                ))}
            </ul>
        </div>
    );
};

export default CategoryManagement;
