import React, { useEffect } from "react";
import { NavDropdown } from "react-bootstrap";
import { connect } from 'react-redux'; // Import connect
import { fetchCategories } from '../store/actions/categoryActions'; // Import your Redux action
import SubcategoriesDropdown from "./SubcategoriesDropdown";
import { HOST } from "../constants";

function CategoriesDropdown({ categories, fetchCategories }) {
    useEffect(() => {
        // Fetch categories for the current category using Redux action
        if (!categories || categories.length === 0) {
            fetchCategories();
        }
    }, [categories, fetchCategories]);

    return (
        <NavDropdown title="Product Categories" id="category-dropdown">
            {categories.map((category) => (
                <NavDropdown.Item
                    key={category.id}
                    href={`#category/${category.id}`}
                >
                    {category.name}
                    <SubcategoriesDropdown categoryId={category.id} />
                </NavDropdown.Item>
            ))}
        </NavDropdown>
    );
}

const mapStateToProps = (state) => ({
    categories: state.category.categories, // Access categories from Redux store
});

export default connect(mapStateToProps, { fetchCategories })(CategoriesDropdown);
