import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import axios from 'axios';
import { HOST } from '../../constants';

const ProductForm = ({ onSubmit, categories, subcategories }) => {
    const { register, handleSubmit, reset } = useForm();
    const [photoPath, setPhotoPath] = useState(''); // State to store the photo path

    const handleFormSubmit = async (data) => {
        data.CategoryId = parseInt(data.CategoryId);
        data.SubcategoryId = parseInt(data.SubcategoryId);


    };

    // Function to handle file input change and get the file path


    return (
        <form onSubmit={handleSubmit(handleFormSubmit)}>
            <input
                type="text"
                placeholder="Product Name"
                {...register('name', { required: true })}
            />

            <textarea
                placeholder="Product Description"
                {...register('description', { required: true })}
            />

            <input
                type="number"
                placeholder="Product Price"
                {...register('price', { required: true, valueAsNumber: true })}
            />

            {/* File input for Photography URL */}


            <select {...register('CategoryId', { required: true, })}>

                {categories.map((category) => (
                    <option key={category.id} value={category.id}>
                        {category.name}
                    </option>
                ))}
            </select>

            <select {...register('SubcategoryId', { required: true,  })}>
                <option value="">Select Subcategory</option>
                {subcategories.map((subcategory) => (
                    <option key={subcategory.id} value={subcategory.id}>
                        {subcategory.name}
                    </option>
                ))}
            </select>

            <button type="submit">Add Product</button>
        </form>
    );
};

export default ProductForm;
