import {React,useState} from 'react';
import { useForm } from 'react-hook-form';


const SubcategoryForm = ({ onSubmit, categories }) => {
    const { register, handleSubmit, reset } = useForm();
    const [selectedCategoryId, setSelectedCategoryId] = useState(''); // State to hold the selected category ID

    const handleFormSubmit = (data) => {
        // Ensure the selectedCategoryId is a number (convert if needed)
        const categoryId = isNaN(selectedCategoryId) ? parseInt(selectedCategoryId) : selectedCategoryId;

        // Combine the selected category ID with other form data
        const formData = {
            ...data,
            CategoryId: categoryId, // Use the selected category ID
        };

        onSubmit(formData);
        reset();
    };

    return (
        <form onSubmit={handleSubmit(handleFormSubmit)}>
            <input type="text" {...register('name', { required: true })} placeholder="Subcategory Name" />
            <select
                {...register('CategoryId', { required: true })}
                value={selectedCategoryId} // Set the selected value
                onChange={(e) => setSelectedCategoryId(e.target.value)} // Update the selected category ID
            >
                <option value="">Select a Category</option>
                {categories.map((category) => (
                    <option key={category.category_id} value={category.category_id}>
                        {category.name}
                    </option>
                ))}
            </select>
            <button type="submit">Add Subcategory</button>
        </form>
    );
};

export default SubcategoryForm;
