import React from 'react';
import { useForm, Controller } from 'react-hook-form';

const ProductForm = ({ onSubmit, categories, subcategories }) => {
    const {register, handleSubmit,reset, control } = useForm();
    const handleFormSubmit = (data) => {
        onSubmit(data); // Call the onSubmit prop with the form data
        reset(); // Reset the form after submission
    };
    return (
        <form onSubmit={handleSubmit(onSubmit)}>
            <input
                type="text"
                placeholder="Product Name"
                {...register('name', { required: true })}
            />

            <textarea
                placeholder="Product Description"
                {...register('description', { required: true })}
            />

            <input
                type="number"
                placeholder="Product Price"
                {...register('price', { required: true, valueAsNumber: true })}
            />

            <input
                type="text"
                placeholder="Photograph URL"
                {...register('photograph_url', { required: true })}
            />

            <select {...register('categoryId', { required: true })}>
                <option value="">Select Category</option>
                {categories.map((category) => (
                    <option key={category.id} value={category.id}>
                        {category.name}
                    </option>
                ))}
            </select>

            <select {...register('subcategoryId', { required: true })}>
                <option value="">Select Subcategory</option>
                {subcategories.map((subcategory) => (
                    <option key={subcategory.id} value={subcategory.id}>
                        {subcategory.name}
                    </option>
                ))}
            </select>

            <button type="submit">Add Product</button>
        </form>
    );
};

export default ProductForm;
