import React, { useState, useEffect } from "react";
import { NavDropdown } from "react-bootstrap";
import axios from "axios";
import SubcategoriesDropdown from "./SubcategoriesDropdown";
import HOST from "../constants";
function CategoriesDropdown() {
    const [categories, setCategories] = useState([]);
    const [openCategory, setOpenCategory] = useState(null);

    useEffect(() => {
        // Fetch categories from the API
        axios.get(HOST+`/api/categories`)
            .then((response) => {
                setCategories(response.data);
            })
            .catch((error) => {
                console.error(`Error fetching categories: `, error);
            });
    }, []);

    const toggleSubcategories = (category) => {
        // Toggle the open/close state of subcategories for the clicked category
        if (openCategory === category.id) {
            setOpenCategory(null); // Close subcategories if already open
        } else {
            setOpenCategory(category.id);
        }
    };

    return (
        <NavDropdown title="Product Categories" id="category-dropdown">
            {categories.map((category) => (
                <NavDropdown.Item
                    key={category.id}
                    href={`#category/${category.id}`}
                    onClick={() => toggleSubcategories(category)}
                >
                    {category.name}
                    {openCategory === category.id && (
                        <SubcategoriesDropdown categoryId={category.id} />
                    )}
                </NavDropdown.Item>
            ))}
        </NavDropdown>
    );
}


export default CategoriesDropdown;
