import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchCategories } from '../store/actions/categoryActions';
import SubcategoriesDropdown from './SubcategoriesDropdown';

function CategoriesDropdown() {
    const dispatch = useDispatch();
    const categories = useSelector((state) => state.category.categories); // Assuming you named your reducer category

    useEffect(() => {
        dispatch(fetchCategories());
    }, [dispatch]);

    return (
        <div>
            {categories.map((category) => (
                <div key={category.id}>
                    <h3>{category.name}</h3>
                    <SubcategoriesDropdown categoryId={category.id} />
                </div>
            ))}
        </div>
    );
}

export default CategoriesDropdown;
