import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchSubcategories } from '../store/actions/categoryActions';

function SubcategoriesDropdown({ categoryId }) {
    const dispatch = useDispatch();
    const subcategories = useSelector((state) => state.category.subcategories); // Assuming you named your reducer category

    useEffect(() => {
        dispatch(fetchSubcategories(categoryId));
    }, [dispatch, categoryId]);

    return (
        <div>
            {subcategories.map((subcategory) => (
                <div key={subcategory.id}>
                    <p>{subcategory.name}</p>
                </div>
            ))}
        </div>
    );
}

export default SubcategoriesDropdown;
