import React, { useState, useEffect } from "react";
import { NavDropdown } from "react-bootstrap";
import axios from "axios";

function CategoriesDropdown() {
    const [categories, setCategories] = useState([]);
    const [openCategory, setOpenCategory] = useState(null);

    useEffect(() => {
        // Fetch categories from the API
        axios.get("http://localhost:3001/api/categories")
            .then((response) => {
                setCategories(response.data);
            })
            .catch((error) => {
                console.error("Error fetching categories:", error);
            });
    }, []);

    const toggleSubcategories = (category) => {
        // Toggle the open/close state of subcategories for the clicked category
        if (openCategory === category.id) {
            setOpenCategory(null); // Close subcategories if already open
        } else {
            setOpenCategory(category.id);
        }
    };

    return (
        <NavDropdown title="Product Categories" id="category-dropdown">
            {categories.map((category) => (
                <NavDropdown.Item
                    key={category.id}
                    href={`#category/${category.id}`}
                    onClick={() => toggleSubcategories(category)}
                >
                    {category.name}
                    {openCategory === category.id && (
                        <SubcategoriesDropdown categoryId={category.id} />
                    )}
                </NavDropdown.Item>
            ))}
        </NavDropdown>
    );
}

function SubcategoriesDropdown({ categoryId }) {
    const [subcategories, setSubcategories] = useState([]);

    useEffect(() => {
        // Fetch subcategories for the current category from the API
        axios.get(`http://localhost:3001/api/categories/${categoryId}/subcategories`)
            .then((response) => {
                setSubcategories(response.data);
            })
            .catch((error) => {
                console.error("Error fetching subcategories:", error);
            });
    }, [categoryId]);

    return (
        <NavDropdown.Menu>
            {subcategories.map((subcategory) => (
                <NavDropdown.Item key={subcategory.id} href={`#subcategory/${subcategory.id}`}>
                    {subcategory.name}
                </NavDropdown.Item>
            ))}
        </NavDropdown.Menu>
    );
}

export default CategoriesDropdown;
