import React, {useState}  from "react";
import { Container, Row, Col, Navbar, Nav, NavDropdown, Card } from 'react-bootstrap';
function MainPage() {
    const [selectedCategory, setSelectedCategory] = useState(null);

    // Dummy product data (replace with data fetched from MySQL)
    const products = [
        { id: 1, name: 'Product 1', category: 'Category 1' },
        { id: 2, name: 'Product 2', category: 'Category 2' },
        // Add more product data here
    ];

    // Get unique categories from product data
    const categories = [...new Set(products.map((product) => product.category))];

    // Filter products based on the selected category
    const filteredProducts = selectedCategory
        ? products.filter((product) => product.category === selectedCategory)
        : products;

    return (
        <div className="main-page">
            {/* Top Navigation Bar */}
            <nav className="top-nav">
                <div className="logo">Online Store</div>
                <ul className="nav-links">
                    <li><a href="#">Home</a></li>
                    {/* Add more top navigation links */}
                </ul>
            </nav>

            {/* Sidebar */}
            <aside className="sidebar">
                <ul className="category-list">
                    {categories.map((category) => (
                        <li
                            key={category}
                            className={category === selectedCategory ? 'active' : ''}
                            onClick={() => setSelectedCategory(category)}
                        >
                            {category}
                        </li>
                    ))}
                </ul>
            </aside>

            {/* Main Content */}
            <main className="content">
                <div className="product-list">
                    {filteredProducts.map((product) => (
                        <div key={product.id} className="product-card">
                            <h3>{product.name}</h3>
                            <p>{product.category}</p>
                            {/* Add more product details */}
                        </div>
                    ))}
                </div>
            </main>
        </div>
    );
}

export default MainPage;
