import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProductForm from '../forms/ProductForm';
import {HOST} from "../../constants"
const ProductManagement = () => {
    const [products, setProducts] = useState([]);
    const [categories, setCategories] = useState([]);
    const [subcategories, setSubcategories] = useState([]);

    useEffect(() => {
        fetchProducts();
        fetchCategories();
        fetchSubcategories();
    }, []);

    const fetchProducts = async () => {
        try {
            const response = await axios.get(`${HOST}/api/products`);
            setProducts(response.data);
        } catch (error) {
            console.error('Error fetching products:', error);
        }
    };

    const fetchCategories = async () => {
        try {
            const response = await axios.get(`${HOST}/api/categories`);
            setCategories(response.data);
        } catch (error) {
            console.error('Error fetching categories:', error);
        }
    };

    const fetchSubcategories = async () => {
        try {
            const response = await axios.get(`${HOST}/api/subcategories`);
            setSubcategories(response.data);
        } catch (error) {
            console.error('Error fetching subcategories:', error);
        }
    };

    const addProduct = async (data) => {
        try {
            await axios.post(`${HOST}/api/products`, data);
            fetchProducts(); // Refresh the product list
        } catch (error) {
            console.error('Error adding product:', error);
        }
    };

    return (
        <div className="product-management">
            <h1>Product Management</h1>
            <ProductForm onSubmit={addProduct} categories={categories} subcategories={subcategories} />
            <ul>
                {products.map((product) => (
                    <li key={product.id}>{product.name}</li>
                ))}
            </ul>
        </div>
    );
};

export default ProductManagement;
