import React, { useState, useEffect } from "react";
import { NavDropdown } from "react-bootstrap";
import axios from "axios";
import {HOST} from "../constants"
function SubcategoriesDropdown({ categoryId }) {
    const [subcategories, setSubcategories] = useState([]);

    useEffect(() => {
        // Fetch subcategories for the current category from the API
        axios.get(`${HOST}/api/categories/${categoryId}/subcategories`)
            .then((response) => {
                setSubcategories(response.data);
            })
            .catch((error) => {
                console.error("Error fetching subcategories:", error);
            });
    }, [categoryId]);

    return (
        <div>
            {subcategories.map((subcategory) => (
                <NavDropdown.Item key={subcategory.id} href={`#subcategory/${subcategory.id}`}>
                    {subcategory.name}
                </NavDropdown.Item>
            ))}
        </div>
    );
}

export default SubcategoriesDropdown;
